import UIKit

enum Characters {
    
    // MARK: Use cases
    
    enum GetCharacters {
        struct Response {
            let results: [CharacterModel]
            let favorites: [FavoriteCharacterEntity]
        }
        struct ViewModel {
            var displayedCharacters: [DisplayedCharacter]
        }
    }
    
    enum SearchCharacters {
        struct Request {
            let searchText: String
        }
    }
    
    enum SaveInFavorite {
        struct Request {
            let indexPath: IndexPath
        }
    }
    
    enum RemoveFromFavorite {
        struct Request {
            let indexPath: IndexPath
        }
    }
    
    struct DisplayedCharacter {
        let name: String
        let isFavorited: Bool
    }
    
    enum Error {
        case emptyList
        case unexpectedError
        case database
        case notConnectedToInternet
        
        var message: String {
            switch self {
            case .emptyList:
                return "Não encontramos nenhum héroi"
            case .unexpectedError:
                return ErrorConstants.unexpectedError
            case .database:
                return ErrorConstants.database
            case .notConnectedToInternet:
                return ErrorConstants.notConnectedToInternet
            }
        }
    }
}
